function applyCharMap(map, text) {
    let out = '';
    for (const c of text.split('')) {
        if (map[c] !== undefined) out += map[c];
        else if (map[c.toLowerCase()] !== undefined) out += map[c.toLowerCase()];
        else out += c;
    }
    return out;
}

